INSERT INTO qa_questions (id, slug, section_id, section_title, question, fingerprint, status, created_at) VALUES
  ('a1000001-0000-0000-0000-000000000001', '_homepage', '', '', '我是完全新手，根本不懂 AI，適合用 OpenClaw 嗎？', 'fp_seed_h01', 'visible', '2026-02-20 09:15:00+00'),
  ('a1000001-0000-0000-0000-000000000002', '_homepage', '', '', '跟 Claude Desktop 的 MCP 有關係嗎？', 'fp_seed_h02', 'visible', '2026-02-21 14:30:00+00'),
  ('a1000001-0000-0000-0000-000000000003', '_homepage', '', '', '有 LINE 版本嗎？我不想裝 Telegram', 'fp_seed_h03', 'visible', '2026-02-22 11:08:00+00'),
  ('a1000001-0000-0000-0000-000000000004', '_homepage', '', '', '公司電腦不能裝軟體，雲端方案可以用嗎？', 'fp_seed_h04', 'visible', '2026-02-23 16:45:00+00'),
  ('a1000001-0000-0000-0000-000000000005', '_homepage', '', '', 'OpenClaw 跟 n8n 有什麼差別？', 'fp_seed_h05', 'visible', '2026-02-24 10:20:00+00');
INSERT INTO qa_answers (id, question_id, answer, fingerprint, helpful_count, status, created_at) VALUES
  ('b1000001-0000-0000-0000-000000000001', 'a1000001-0000-0000-0000-000000000001', '適合。會用 ChatGPT 就夠了，OpenClaw 不需要寫程式，照著教學一步一步來就好。', 'fp_seed_ans01', 12, 'visible', '2026-02-20 10:02:00+00'),
  ('b1000001-0000-0000-0000-000000000002', 'a1000001-0000-0000-0000-000000000002', 'MCP 是 Anthropic 推的協定，OpenClaw 是獨立框架，不需要 Claude Desktop。概念上類似，都是讓 AI 可以呼叫工具，但兩個是不同東西。', 'fp_seed_ans02', 8, 'visible', '2026-02-21 15:10:00+00'),
  ('b1000001-0000-0000-0000-000000000003', 'a1000001-0000-0000-0000-000000000003', '官方目前只支援 Telegram。LINE 的話要透過 webhook 橋接，社群有人做過，但沒有官方教學。Telegram 其實裝起來 2 分鐘搞定，不麻煩。', 'fp_seed_ans03', 6, 'visible', '2026-02-22 11:55:00+00'),
  ('b1000001-0000-0000-0000-000000000004', 'a1000001-0000-0000-0000-000000000004', '可以！雲端方案不用裝任何東西在本機，只要有瀏覽器就能用。看「安裝指南」那篇，選雲端部署那個選項。', 'fp_seed_ans04', 10, 'visible', '2026-02-23 17:20:00+00'),
  ('b1000001-0000-0000-0000-000000000005', 'a1000001-0000-0000-0000-000000000005', 'n8n 是視覺化拉線的自動化工具，比較接近 Zapier。OpenClaw 是 AI-native，你用自然語言說「幫我做XX」，它自己想怎麼做。定位不太一樣。', 'fp_seed_ans05', 15, 'visible', '2026-02-24 11:05:00+00');
INSERT INTO qa_questions (id, slug, section_id, section_title, question, fingerprint, status, created_at) VALUES
  ('a2000001-0000-0000-0000-000000000001', 'why-openclaw', '', '', 'OpenClaw 是免費的嗎？', 'fp_seed_w01', 'visible', '2026-02-20 13:22:00+00'),
  ('a2000001-0000-0000-0000-000000000002', 'why-openclaw', '', '', '需要一直開著電腦嗎？', 'fp_seed_w02', 'visible', '2026-02-21 09:45:00+00'),
  ('a2000001-0000-0000-0000-000000000003', 'why-openclaw', '', '', '用 Notion AI 或 Gemini 的進階版不就好了？', 'fp_seed_w03', 'visible', '2026-02-22 15:33:00+00'),
  ('a2000001-0000-0000-0000-000000000004', 'why-openclaw', '', '', '資料安不安全？我的資料會不會傳到外部？', 'fp_seed_w04', 'visible', '2026-02-23 08:10:00+00');
INSERT INTO qa_answers (id, question_id, answer, fingerprint, helpful_count, status, created_at) VALUES
  ('b2000001-0000-0000-0000-000000000001', 'a2000001-0000-0000-0000-000000000001', '軟體本身免費，但你需要一個 LLM 的 API Key 才能讓它「思考」。新手推薦先用 Google AI Studio，有很夠用的免費額度，不需要信用卡。', 'fp_seed_ans11', 18, 'visible', '2026-02-20 14:00:00+00'),
  ('b2000001-0000-0000-0000-000000000002', 'a2000001-0000-0000-0000-000000000002', '本地安裝版需要電腦開著。如果你想隨時待命，可以部署到雲端（Zeabur 那個方案），這樣電腦關掉也能跑。', 'fp_seed_ans12', 9, 'visible', '2026-02-21 10:20:00+00'),
  ('b2000001-0000-0000-0000-000000000003', 'a2000001-0000-0000-0000-000000000003', '差在「能不能串接你自己的工具」。Notion AI 只能在 Notion 裡面用，Gemini 也是封閉的。OpenClaw 可以讓你的 Gmail、Drive、Notion 互相溝通，這是本質上的差異。', 'fp_seed_ans13', 22, 'visible', '2026-02-22 16:10:00+00'),
  ('b2000001-0000-0000-0000-000000000004', 'a2000001-0000-0000-0000-000000000004', '本地安裝的話，資料不會離開你的電腦，只有呼叫 LLM 的時候那段文字會送到 API。你授權 OpenClaw 存取哪些服務，就只有那些服務被碰到，其他完全不會動。', 'fp_seed_ans14', 14, 'visible', '2026-02-23 08:50:00+00');
INSERT INTO qa_questions (id, slug, section_id, section_title, question, fingerprint, status, created_at) VALUES
  ('a3000001-0000-0000-0000-000000000001', 'llm-guide', '', '', '都用 free tier 的話，Gemini Flash 夠用嗎？', 'fp_seed_l01', 'visible', '2026-02-21 11:30:00+00'),
  ('a3000001-0000-0000-0000-000000000002', 'llm-guide', '', '', 'OpenRouter 的免費模型品質怎麼樣？', 'fp_seed_l02', 'visible', '2026-02-22 14:15:00+00'),
  ('a3000001-0000-0000-0000-000000000003', 'llm-guide', '', '', '我已經有 ChatGPT Plus 訂閱，可以不用 API 嗎？', 'fp_seed_l03', 'visible', '2026-02-23 09:40:00+00'),
  ('a3000001-0000-0000-0000-000000000004', 'llm-guide', '', '', 'Token 費用要怎麼估算？會不會不小心花很多？', 'fp_seed_l04', 'visible', '2026-02-24 13:55:00+00');
INSERT INTO qa_answers (id, question_id, answer, fingerprint, helpful_count, status, created_at) VALUES
  ('b3000001-0000-0000-0000-000000000001', 'a3000001-0000-0000-0000-000000000001', '日常使用完全夠。每分鐘 15 次、每天 1500 次請求免費。除非你在跑大量自動化，不然根本用不完。', 'fp_seed_ans21', 13, 'visible', '2026-02-21 12:05:00+00'),
  ('b3000001-0000-0000-0000-000000000002', 'a3000001-0000-0000-0000-000000000002', '測試功能沒問題，像 Llama 3.3 70B 或 Mistral 都還不錯。但如果正式使用，建議還是付費模型，品質穩定很多。', 'fp_seed_ans22', 7, 'visible', '2026-02-22 14:50:00+00'),
  ('b3000001-0000-0000-0000-000000000003', 'a3000001-0000-0000-0000-000000000003', '不行，ChatGPT Plus 是訂閱制的「聊天介面」，OpenClaw 需要的是 API 存取權，這兩個是分開計費的，要另外申請 OpenAI API Key。', 'fp_seed_ans23', 25, 'visible', '2026-02-23 10:15:00+00'),
  ('b3000001-0000-0000-0000-000000000004', 'a3000001-0000-0000-0000-000000000004', '一般日常使用，一個月大概 USD $1-3 左右。可以在 API 後台設定「Hard Limit」，超過就自動停止，不用擔心爆掉。建議先設 $5 上限測試看看。', 'fp_seed_ans24', 19, 'visible', '2026-02-24 14:30:00+00');
INSERT INTO qa_questions (id, slug, section_id, section_title, question, fingerprint, status, created_at) VALUES
  ('a4000001-0000-0000-0000-000000000001', 'ai-api-key-guide', '', '', 'API Key 最安全存在哪裡？', 'fp_seed_k01', 'visible', '2026-02-21 16:20:00+00'),
  ('a4000001-0000-0000-0000-000000000002', 'ai-api-key-guide', '', '', '不小心把 Key 傳到 GitHub 了怎麼辦？', 'fp_seed_k02', 'visible', '2026-02-22 09:08:00+00'),
  ('a4000001-0000-0000-0000-000000000003', 'ai-api-key-guide', '', '', '一個 Key 可以同時在多台電腦用嗎？', 'fp_seed_k03', 'visible', '2026-02-23 11:30:00+00');
INSERT INTO qa_answers (id, question_id, answer, fingerprint, helpful_count, status, created_at) VALUES
  ('b4000001-0000-0000-0000-000000000001', 'a4000001-0000-0000-0000-000000000001', '不要存在 Notion、Google Keep 這類雲端筆記。推薦用 1Password 或 Bitwarden（後者免費），或是只存在本機的 .env 檔裡，並確保 .gitignore 有加上 .env。', 'fp_seed_ans31', 17, 'visible', '2026-02-21 16:55:00+00'),
  ('b4000001-0000-0000-0000-000000000002', 'a4000001-0000-0000-0000-000000000002', '立刻去對應平台「Revoke（撤銷）」那把 Key，然後重新申請一把。GitHub 的爬蟲幾秒內就會掃到公開的 Key，不能抱僥倖。以後記得在 .gitignore 加上 .env 就好。', 'fp_seed_ans32', 31, 'visible', '2026-02-22 09:45:00+00'),
  ('b4000001-0000-0000-0000-000000000003', 'a4000001-0000-0000-0000-000000000003', '可以，同一把 Key 在多台機器用是沒問題的。但建議用了哪些地方自己記好，萬一有問題比較好追蹤。', 'fp_seed_ans33', 8, 'visible', '2026-02-23 12:05:00+00');
INSERT INTO qa_questions (id, slug, section_id, section_title, question, fingerprint, status, created_at) VALUES
  ('a5000001-0000-0000-0000-000000000001', 'install-openclaw-macos', '', '', 'Python 版本一定要 3.11 嗎？3.12 不行？', 'fp_seed_m01', 'visible', '2026-02-21 10:15:00+00'),
  ('a5000001-0000-0000-0000-000000000002', 'install-openclaw-macos', '', '', 'Homebrew 安裝卡在那裡很久是正常的嗎？', 'fp_seed_m02', 'visible', '2026-02-22 13:40:00+00'),
  ('a5000001-0000-0000-0000-000000000003', 'install-openclaw-macos', '', '', 'M1 晶片有遇到什麼問題嗎？', 'fp_seed_m03', 'visible', '2026-02-23 14:22:00+00'),
  ('a5000001-0000-0000-0000-000000000004', 'install-openclaw-macos', '', '', '已經有裝 Python 3.10 了，要解除安裝嗎？', 'fp_seed_m04', 'visible', '2026-02-24 09:18:00+00');
INSERT INTO qa_answers (id, question_id, answer, fingerprint, helpful_count, status, created_at) VALUES
  ('b5000001-0000-0000-0000-000000000001', 'a5000001-0000-0000-0000-000000000001', '3.12 以上部分依賴套件會裝不上（特別是 C extension 類的）。建議照文章裝 3.11，之後官方說支援再升級。', 'fp_seed_ans41', 20, 'visible', '2026-02-21 10:50:00+00'),
  ('b5000001-0000-0000-0000-000000000002', 'a5000001-0000-0000-0000-000000000002', '正常，第一次安裝要下載 Xcode Command Line Tools，可能要等 5-15 分鐘。看到進度條在動就沒問題，不要關終端機。', 'fp_seed_ans42', 16, 'visible', '2026-02-22 14:10:00+00'),
  ('b5000001-0000-0000-0000-000000000003', 'a5000001-0000-0000-0000-000000000003', '主要的坑是 Homebrew 安裝完要手動跑一行指令讓 brew 生效（文章有寫），漏掉的話 brew 指令會找不到。其他基本沒問題。', 'fp_seed_ans43', 11, 'visible', '2026-02-23 15:00:00+00'),
  ('b5000001-0000-0000-0000-000000000004', 'a5000001-0000-0000-0000-000000000004', '不用解除安裝，pyenv 會幫你管版本。裝好 pyenv 之後，在 OpenClaw 的資料夾下 `pyenv local 3.11.x` 就能指定用 3.11，不影響系統原本的 Python。', 'fp_seed_ans44', 24, 'visible', '2026-02-24 09:55:00+00');
INSERT INTO qa_questions (id, slug, section_id, section_title, question, fingerprint, status, created_at) VALUES
  ('a6000001-0000-0000-0000-000000000001', 'install-openclaw-windows', '', '', 'WSL 跟直接在 Windows 裝有什麼差？', 'fp_seed_win01', 'visible', '2026-02-21 14:55:00+00'),
  ('a6000001-0000-0000-0000-000000000002', 'install-openclaw-windows', '', '', '我沒有管理員權限，還能裝嗎？', 'fp_seed_win02', 'visible', '2026-02-22 10:30:00+00'),
  ('a6000001-0000-0000-0000-000000000003', 'install-openclaw-windows', '', '', 'Windows 11 Home 跟 Pro 有差嗎？', 'fp_seed_win03', 'visible', '2026-02-23 16:15:00+00');
INSERT INTO qa_answers (id, question_id, answer, fingerprint, helpful_count, status, created_at) VALUES
  ('b6000001-0000-0000-0000-000000000001', 'a6000001-0000-0000-0000-000000000001', 'WSL 跑的是 Linux，套件相容性好很多，踩坑機率低。如果你之後想玩 Docker，直接選 WSL 就對了。純 Windows 安裝比較簡單，但遇到奇怪的套件問題時比較難 debug。', 'fp_seed_ans51', 18, 'visible', '2026-02-21 15:30:00+00'),
  ('b6000001-0000-0000-0000-000000000002', 'a6000001-0000-0000-0000-000000000002', 'pyenv-win 可以安裝在使用者目錄下，不需要管理員權限。但裝 WSL 要有管理員。所以沒有管理員就用原生 Windows + pyenv-win 那條路。', 'fp_seed_ans52', 13, 'visible', '2026-02-22 11:05:00+00'),
  ('b6000001-0000-0000-0000-000000000003', 'a6000001-0000-0000-0000-000000000003', '沒有差，兩個都可以裝 WSL2。Home 版啟用 WSL 的方式一樣，跟著文章步驟做就好。', 'fp_seed_ans53', 7, 'visible', '2026-02-23 16:50:00+00');
INSERT INTO qa_questions (id, slug, section_id, section_title, question, fingerprint, status, created_at) VALUES
  ('a7000001-0000-0000-0000-000000000001', 'openclaw-first-run', '', '', 'Gateway 是什麼？為什麼要開這個？', 'fp_seed_fr01', 'visible', '2026-02-22 09:55:00+00'),
  ('a7000001-0000-0000-0000-000000000002', 'openclaw-first-run', '', '', '第一次設定選錯了可以重來嗎？', 'fp_seed_fr02', 'visible', '2026-02-23 12:40:00+00'),
  ('a7000001-0000-0000-0000-000000000003', 'openclaw-first-run', '', '', 'Telegram bot token 哪裡拿？', 'fp_seed_fr03', 'visible', '2026-02-24 08:30:00+00'),
  ('a7000001-0000-0000-0000-000000000004', 'openclaw-first-run', '', '', '回應很慢怎麼辦？等了 30 秒才有回', 'fp_seed_fr04', 'visible', '2026-02-24 15:10:00+00');
INSERT INTO qa_answers (id, question_id, answer, fingerprint, helpful_count, status, created_at) VALUES
  ('b7000001-0000-0000-0000-000000000001', 'a7000001-0000-0000-0000-000000000001', 'Gateway 是 OpenClaw 在本地開的一個 API 服務，Telegram bot 和其他客戶端都透過它跟 LLM 溝通。可以把它想成「本地的 AI 中轉站」，不開的話 bot 就收不到訊息。', 'fp_seed_ans61', 21, 'visible', '2026-02-22 10:30:00+00'),
  ('b7000001-0000-0000-0000-000000000002', 'a7000001-0000-0000-0000-000000000002', '可以，隨時 `openclaw config` 重新設定，或直接編輯 `~/.openclaw/config.yaml`，改完存檔就生效了。', 'fp_seed_ans62', 15, 'visible', '2026-02-23 13:15:00+00'),
  ('b7000001-0000-0000-0000-000000000003', 'a7000001-0000-0000-0000-000000000003', 'Telegram 裡搜尋 @BotFather，點開後傳 /newbot，照著指示填名字，幾十秒就能拿到 token，非常簡單。', 'fp_seed_ans63', 12, 'visible', '2026-02-24 09:05:00+00'),
  ('b7000001-0000-0000-0000-000000000004', 'a7000001-0000-0000-0000-000000000004', '第一次呼叫會比較慢，模型要「冷啟動」。另外 Gemini 免費版在高峰時段本來就比較慢。如果一直這樣，可以試試 Gemini Flash 換成 Pro，或換 OpenRouter 路由到其他模型。', 'fp_seed_ans64', 9, 'visible', '2026-02-24 15:45:00+00');
INSERT INTO qa_questions (id, slug, section_id, section_title, question, fingerprint, status, created_at) VALUES
  ('a8000001-0000-0000-0000-000000000001', 'openclaw-first-skill', '', '', 'YAML 縮排出錯不知道怎麼找問題', 'fp_seed_fs01', 'visible', '2026-02-23 10:05:00+00'),
  ('a8000001-0000-0000-0000-000000000002', 'openclaw-first-skill', '', '', 'Skill 可以定時自動執行嗎？', 'fp_seed_fs02', 'visible', '2026-02-24 11:22:00+00'),
  ('a8000001-0000-0000-0000-000000000003', 'openclaw-first-skill', '', '', '完成後想把結果傳到 Telegram 可以嗎？', 'fp_seed_fs03', 'visible', '2026-02-24 14:00:00+00');
INSERT INTO qa_answers (id, question_id, answer, fingerprint, helpful_count, status, created_at) VALUES
  ('b8000001-0000-0000-0000-000000000001', 'a8000001-0000-0000-0000-000000000001', '推薦用 VS Code 開 YAML，有套件會自動標出縮排錯誤。或是貼到 yamllint.com 線上驗證也很快。YAML 只能用空格不能用 Tab，這是最常見的坑。', 'fp_seed_ans71', 16, 'visible', '2026-02-23 10:40:00+00'),
  ('b8000001-0000-0000-0000-000000000002', 'a8000001-0000-0000-0000-000000000002', '可以！在 Skill 的 trigger 區塊把 type 改成 schedule，然後填 cron 格式的時間。例如 `"0 9 * * 1"` 就是每週一早上 9 點自動跑。', 'fp_seed_ans72', 20, 'visible', '2026-02-24 11:58:00+00'),
  ('b8000001-0000-0000-0000-000000000003', 'a8000001-0000-0000-0000-000000000003', '可以，在 Skill 的最後加一個 action，type 用 `telegram.send`，指定你的 chat id 就好。這樣每次 Skill 跑完會自動傳一則 Telegram 通知給你。', 'fp_seed_ans73', 14, 'visible', '2026-02-24 14:35:00+00');
INSERT INTO qa_questions (id, slug, section_id, section_title, question, fingerprint, status, created_at) VALUES
  ('a9000001-0000-0000-0000-000000000001', 'openclaw-skill', '', '', 'Skill 跟 ChatGPT 的 Custom Instructions 差在哪？', 'fp_seed_sk01', 'visible', '2026-02-22 15:00:00+00'),
  ('a9000001-0000-0000-0000-000000000002', 'openclaw-skill', '', '', 'Skill 失敗了怎麼 debug？', 'fp_seed_sk02', 'visible', '2026-02-23 08:50:00+00'),
  ('a9000001-0000-0000-0000-000000000003', 'openclaw-skill', '', '', '一個 Skill 裡面可以呼叫另一個 Skill 嗎？', 'fp_seed_sk03', 'visible', '2026-02-24 12:30:00+00');
INSERT INTO qa_answers (id, question_id, answer, fingerprint, helpful_count, status, created_at) VALUES
  ('b9000001-0000-0000-0000-000000000001', 'a9000001-0000-0000-0000-000000000001', 'Custom Instructions 只是調整 AI 回話的風格和個性，本身不會「做任何事情」。Skill 可以真的執行動作：存檔、發 email、搜尋網路、傳 Telegram。完全不同的東西。', 'fp_seed_ans81', 27, 'visible', '2026-02-22 15:35:00+00'),
  ('b9000001-0000-0000-0000-000000000002', 'a9000001-0000-0000-0000-000000000002', '`openclaw run <skill-name> --debug` 可以看每個 action 的詳細 log，包含輸入輸出和錯誤訊息，比較好抓問題在哪一步。', 'fp_seed_ans82', 18, 'visible', '2026-02-23 09:25:00+00'),
  ('b9000001-0000-0000-0000-000000000003', 'a9000001-0000-0000-0000-000000000003', '可以，用 `skill.run` 這個 action 就能在 Skill 裡面呼叫另一個 Skill，方便把複雜流程拆成小模組重複使用。', 'fp_seed_ans83', 11, 'visible', '2026-02-24 13:05:00+00');
INSERT INTO qa_questions (id, slug, section_id, section_title, question, fingerprint, status, created_at) VALUES
  ('aa000001-0000-0000-0000-000000000001', 'openclaw-agent', '', '', 'Agent 會不會刪錯東西？有點怕', 'fp_seed_ag01', 'visible', '2026-02-22 16:45:00+00'),
  ('aa000001-0000-0000-0000-000000000002', 'openclaw-agent', '', '', 'Agent 跟 Skill 要怎麼選要用哪個？', 'fp_seed_ag02', 'visible', '2026-02-23 13:20:00+00'),
  ('aa000001-0000-0000-0000-000000000003', 'openclaw-agent', '', '', 'Agent 的記憶是永久的嗎？怎麼清除？', 'fp_seed_ag03', 'visible', '2026-02-24 10:05:00+00'),
  ('aa000001-0000-0000-0000-000000000004', 'openclaw-agent', '', '', 'ReAct 這個名字是怎麼來的？', 'fp_seed_ag04', 'visible', '2026-02-24 16:30:00+00');
INSERT INTO qa_answers (id, question_id, answer, fingerprint, helpful_count, status, created_at) VALUES
  ('ba000001-0000-0000-0000-000000000001', 'aa000001-0000-0000-0000-000000000001', '新手強烈建議在設定裡開 `confirm_before_action: true`，這樣 Agent 每做一個動作前都會先問你「要不要繼續」，你可以隨時喊停。等熟悉之後再拿掉這個限制。', 'fp_seed_ans91', 32, 'visible', '2026-02-22 17:20:00+00'),
  ('ba000001-0000-0000-0000-000000000002', 'aa000001-0000-0000-0000-000000000002', '固定流程用 Skill，比較可預測、也比較省 token。需要 AI 自己判斷「怎麼做」、流程不固定的任務才用 Agent。初學者先從 Skill 學起穩一點。', 'fp_seed_ans92', 23, 'visible', '2026-02-23 13:55:00+00'),
  ('ba000001-0000-0000-0000-000000000003', 'aa000001-0000-0000-0000-000000000003', '記憶預設只在對話 session 裡面存在，重啟後會清掉。如果你有開 persistent memory，可以在設定裡找到記憶檔案直接刪除，或下 `openclaw memory clear` 清掉。', 'fp_seed_ans93', 10, 'visible', '2026-02-24 10:40:00+00'),
